    public function CheckSubScriptionByMobile(Request $request)
    {
        Log::info("[Start:CheckSubScriptionByMobile]");
        $data = [
            "Mobile" => session('user')['Mobile'] ?? $request->input('Mobile'),
        ];
        $url = config('envpath.Booking_Service') . "/ServiceManagement/CheckSubScriptionByMobile";
        $response = postToApi($url, $data);

        session(['SubScriptionStatus' => $response['ResultData']['SubScriptionStatus']]);

        Log::info($data);
        Log::info("[End:CheckSubScriptionByMobile]");
        return response()->json($response);
        // return response()->json([
        //     'Result' => "1",
        //     'Message' => "ดึงข้อมูลสำเร็จ",
        //     'ResultData' => ['SubScriptionStatus' => "Y"]
        // ]);
    }

    public function SaveSubScriptionByEventID(Request $request)
    {
        Log::info("[Start:SaveSubScriptionByEventID]");
        $data = [
            "EventID" => $request->input('EventID'),
            "CusMemberID" => session('MemberID') ?? '',
            "MemberStatus" => $request->input('MemberStatus'),
            "Mobile" => $request->input('Mobile') ?? '0897008900',
            "Email" => $request->input('Email') ?? 'kaitom171@gmail.com',
            "IPAddress" => $request->ip()
        ];
        $url = config('envpath.Booking_Service') . "/ServiceManagement/SaveSubScriptionByEventID";
        $response = postToApi($url, $data);
        Log::info($response);

        session(['SubScriptionStatus' => 'Y']);
        Log::info("[End:SaveSubScriptionByEventID]");
        return response()->json($response);
        // return response()->json([
        //     'Result' => "1"
        // ]);
    }


    public function ProfileIndex()
    {
        Log::info("[Start:ProfileIndex]");
        $pathConfig = config('envpath.URL_API');
        $url = config('envpath.URL_API') . '/UtilsManagement/GetTitleName';
        $TitleNames = getFromApi($url);

        if ($TitleNames) {
            $url = $pathConfig . '/GetBusinessTypeList';
            $JuristicTypeList = getFromApi($url);

            $url = $pathConfig . '/GetDepartmentTypeList';
            $DepartmentList = getFromApi($url);

            $url = $pathConfig . '/GetPositionTypeList';
            $PositionList = getFromApi($url);

            $data = [
                "CusMemberID" => session('user')['MemberID'],
            ];

            $url = config('envpath.CA_Server_Publish') . "/GetUserInfoByID";
            $responseBody = postToApi($url, $data);

            Log::info("[End:ProfileIndex]");
            return view("components.AccountSetting.profile", [
                'title' => 'จัดการข้อมูลสมาชิก',
                'TitleNames' => $TitleNames,
                'JuristicTypeList' => $JuristicTypeList,
                'DepartmentList' => $DepartmentList,
                'PositionList' => $PositionList,
                'responseBody' => $responseBody,
            ]);
        } else {
            Log::error("[Error] Connect API failed");
            return view("components.AccountSetting.overview", [
                'title' => '',
                'responseBody' => '',
            ]);
        }
    }
