    <script>
        // Function to handle the remove button click event
        function removeItem(element, index) {
            // Get the parent element (the card) of the remove button
            const card = $(element).closest('.card');

            // Get the parent element (the room-section) of the card
            const section = $('#room-' + index);

            // Remove both the card and the room-section from the DOM
            card.remove();
            section.remove();

            // Open the modal if there are no more rooms
            if ($('.card').length === 0) {
                // Redirect to event
                window.location.href = "{{ route('event') }}"
            } else {
                 // Open the modal if there are no more rooms
                $('#compare').modal('show');
            }
        }

        $('.btn-close').click(function() {
            // check if there are no elements with class 'card'
            if ($('.card').length === 0) {
                // Redirect to event
                window.location.href = "{{ route('event') }}"
            }
        })
    </script>
